import Foundation
import SwiftData

/// Seed data for all NYC subway stations
/// Data sourced from MTA GTFS feeds
struct StationData {

    /// Seeds the database with all NYC subway stations if empty
    @MainActor
    static func seedIfNeeded(modelContext: ModelContext) {
        let descriptor = FetchDescriptor<Station>()
        let existingCount = (try? modelContext.fetchCount(descriptor)) ?? 0

        guard existingCount == 0 else { return }

        for stationInfo in allStations {
            let station = Station(
                name: stationInfo.name,
                lines: stationInfo.lines,
                latitude: stationInfo.latitude,
                longitude: stationInfo.longitude,
                borough: stationInfo.borough
            )
            modelContext.insert(station)
        }

        try? modelContext.save()
    }

    /// Station info tuple for seeding
